            "$lookup": {
                "from": "destinations",
                "localField": "ID",
                "foreignField": "tour_id",
                "as": "dest"
                }
        },
        {"$unwind": "$dest"},
        {"$sort": {"dest.order":-1}},
        {
            "$group": {
                "_id": "$title",
                "data": {"$push": "$dest"}
            }
