const Tours = require('../models/tours');

const Destination = require('../models/destination');

exports.historicalCulturalPage = (req, res) => {
  let lng = req.cookies.lng;
  Tours.aggregate([{
    "$match": {
      name: "historical-cultural"
    }
  }, {
    "$lookup": {
      "from": "destinations",
      "localField": "ID",
      "foreignField": "tour_id",
      "as": "dest"
    }
  }, {
    "$unwind": "$dest"
  }, {
    "$group": {
      "_id": "$title",
      "data": {
        "$push": "$dest"
      }
    }
  }]).exec((error, response) => {
    if (error) {
      console.log(error);
    }

    let result = response[0];
    res.render('historical-cultural.ejs', {
      header_title: result._id[lng],
      data: result.data
    });
  });
};

exports.gastronomicPage = (req, res) => {
  let lng = req.cookies.lng;
  Tours.aggregate([{
    "$match": {
      name: "gastronomic"
    }
  }, {
    "$lookup": {
      "from": "destinations",
      "localField": "ID",
      "foreignField": "tour_id",
      "as": "dest"
    }
  }, {
    "$unwind": "$dest"
  }, {
    "$group": {
      "_id": "$title",
      "data": {
        "$push": "$dest"
      }
    }
  }]).exec((error, response) => {
    if (error) {
      console.log(error);
    }

    let result = response[0];
    res.render('gastronomic.ejs', {
      header_title: result._id[lng],
      data: result.data
    });
  });
};

exports.historicalCulturalSingle = (req, res) => {
  let destination = req.params.tour;
  Destination.findOne({
    name: destination
  }).exec((error, response) => {
    res.render('single_tour.ejs', {
      data: response
    });
  });
};
